export const getRandomQuizQuestion = (excludeIds: number[] = []) => {
  const available = quizBank.filter(q => !excludeIds.includes(q.id));
  const pool = available.length > 0 ? available : quizBank;
  return pool[Math.floor(Math.random() * pool.length)];
};
